 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { AngularFirestore, AngularFirestoreDocument, AngularFirestoreCollection, DocumentReference } from '@angular/fire/firestore';
import { AngularFireFunctions } from '@angular/fire/functions';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Student } from '../../domain/task2_db/student';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../student.service.ts
 */

/*
 * SCHEMA DB student
 *
	{
		date_of_barth: {
			type: 'Date'
		},
		gpa: {
			type: 'Number'
		},
		id: {
			type: 'Number'
		},
		name: {
			type: 'String'
		},
		//RELATIONS
		//EXTERNAL RELATIONS
	}
 *
 */
@Injectable()
export class StudentBaseService {

    private studentCollection: AngularFirestoreCollection<Student>;
    constructor(
        private afs: AngularFirestore,
        private fns: AngularFireFunctions
    ) {
        this.studentCollection = afs.collection<Student>('student');
    }


    // CRUD METHODS

    /**
    * studentService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: Student): Promise<DocumentReference> {
        return this.studentCollection.add(item);
    }

    /**
    * studentService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string) {
        const itemDoc: AngularFirestoreDocument<any> = this.studentCollection.doc(id);
        itemDoc.delete();
    }

    /**
    * studentService.get
    *   @description CRUD ACTION get
    *   @returns student
    *
    */
    get(id: string): AngularFirestoreDocument<Student> {
        return this.afs.doc<Student>('student/' + id);
    }

    /**
    * studentService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<Student[]> {
        return this.afs.collection('student').snapshotChanges().pipe(
            map(actions => actions.map(a => {
                const data = a.payload.doc.data() as Student;
                const id = a.payload.doc.id;
                return { id, ...data };
            }))
        );
    }

    /**
    * studentService.update
    *   @description CRUD ACTION update
    *   @returns student
    *
    */
    update(itemDoc: AngularFirestoreDocument<Student>, item: Student): Promise<void> {
        return itemDoc.update(item);
    }


    // Custom APIs

}
